import React, { Component } from 'react';
 class Counter extends Component {


     render() { 
         let classes= this.getBadge();
         return <div>
             <table class="table">
            <thead>
                <tr>
                <th scope="col">#</th>
                <th scope="col">First</th>
                <th scope="col">Last</th>
                <th scope="col">Handle</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <th scope="row">1</th>
                <td>Mark</td>
                <td>Otto</td>
                <td>@mdo</td>
                </tr>
                <tr>
                <th scope="row">2</th>
                <td>Jacob</td>
                <td>Thornton</td>
                <td>@fat</td>
                </tr>
                <tr>
                <th scope="row">3</th>
                <td colspan="2">Larry the Bird</td>
                <td>@twitter</td>
                </tr>
                </tbody>
</table>    
             <span className={classes}>{this.checkState()}</span>
             <button onClick={() => this.props.onIncrement(this.props.counter)} className="btn btn-outline-primary m-2">+</button>
             <button onClick={()=> this.props.onDecrement(this.props.counter)} className="btn btn-warning m-2">-</button>
             <button onClick={() => this.props.onDelete(this.props.counter.id)} className="btn btn-danger">Delete</button>

         </div>;
     }
     checkState(){
         let {value} =this.props.counter;
         return value===0 ? 'Zero' : value;
    }
    getBadge()
    {
        let badge= "badge m-2 bg-";
        badge += this.props.counter.value===0 ? "warning" : "primary";
        return badge;
     }
 }

 export default Counter;
