import React, { Component } from 'react';
import Counter from './counter';
class Counters extends Component {
    state ={
        counters :[
            { id: 1, value: 0 },
            { id: 2, value: 3 },
            { id: 3, value: 0 },
            { id: 4, value: 0 }
        ] 
    };
    deleteHandle = counterId => {
        const {counter}=this.state.counters.filter(c => c.id !== counter.id);
        this.setState({counters});
        // console.log("im in delete",{counterId});
    };
    render() { 
        return (
        <div>
            {this.state.counters.map(counter => <Counter 
            key={counter.id} 
            onDelete={this.deleteHandle}
            value={counter.value} 
            id={counter.id}/>)}
        </div>
        );
    }
}

export default Counters;
