import React, { Component } from 'react';
 class Counter extends Component {


     render() { 
         let classes= this.getBadge();
         return <div>

             <table class="table table-borderless">
            <thead>
                <tr>
                <th scope="col">Values</th>
                <th scope="col">Increment</th>
                <th scope="col">Decrement</th>
                <th scope="col">Delete</th>
                </tr>
            </thead>
            <tbody>
                <tr>
                <td> <span className={classes}>{this.checkState()}</span></td>
                <td><button onClick={() => this.props.onIncrement(this.props.counter)} className="btn btn-outline-primary m-2">+</button></td>
                <td><button onClick={()=> this.props.onDecrement(this.props.counter)} className="btn btn-warning m-2">-</button></td>
                <td><button onClick={() => this.props.onDelete(this.props.counter.id)} className="btn btn-danger">Delete</button></td>
                </tr>
                </tbody>    
            </table>    





         </div>;
     }
     checkState(){
         let {value} =this.props.counter;
         return value===0 ? 'Zero' : value;
    }
    getBadge()
    {
        let badge= "badge m-2 bg-";
        badge += this.props.counter.value===0 ? "warning" : "primary";
        return badge;
     }
 }

 export default Counter;
