import React, { Component } from 'react';
 class Counter extends Component {
     state ={
         value:this.props.vl
     };
     handleincrement=() =>{
        this.setState({value: this.state.value+1})
     };
     render() { 
         let clases= this.getBadge();
         return <div>
             <span className={clases}>{this.checkState()}</span>
             <button onClick={() => this.handleincrement()} className="btn btn-outline-primary m-2">Increment</button>
         </div>;
     }
     checkState(){
         let {value} =this.state;
         return value===0 ? 'Zero' : value;
    }
    getBadge()
    {
        let badge= "badge m-2 bg-";
        badge += this.state.value===0 ? "warning" : "primary";
        return badge;
     }
 }

 export default Counter;
